(function($){$.fn.panoramah=function(){return this.each(function(index){var photo=$(this);var panorama_width=photo.attr("rel").split(":")[0];var panorama_url=photo.attr("rel").split(":")[1];var img=$("<img src='' />");img.load(function(){photo.css("background","transparent url( '"+panorama_url+"' ) no-repeat");photo.html("");photo.mousemove(function(event){offset=Math.floor((panorama_width-photo.width())*((event.pageX-photo.offset().left)/photo.width()));if(offset<=panorama_width-photo.width()){photo.css("background-position","-"+offset+"px 50%");}});});img.attr("src",panorama_url);});};})(jQuery);
